package loopsinjava;

public class Nested_ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i; int j; int k;
		for(i=0; i<=5; i++) {
		for(j=1; j<=5; j++) {
		for(k=1; k<=5; k++) {
			System.out.println(i+"-"+j+"-"+k);
		}
		}
		}
			

	}

}
